import React from 'react';

import style from './sideNav.css';


const SideNavigation = (props) => {

  let showNav = props.showNav;

  let wrapperState = showNav ? {pointerEvents: 'auto'} : {};
  let bgState = showNav ? {opacity: 1} : {};
  let navState = showNav ? {transform: 'translateX(0)'} : {};
  

  return (
    <div className={style.wrapper} style={wrapperState}>

      <div 
        style={bgState} 
        className={style.bg}
        onClick={() => {
          props.onHideNav && props.onHideNav();
        }}
      ></div>

      <div 
        style={navState} 
        className={style.nav}
      >
        {props.children}
      </div>

      </div>
  );
};

export default SideNavigation;