import React from 'react';

import style from './image.css';

const Image = ({src, onLoad, children, alt, className}) => {

  let onLoadHandler = (e) => {
    onLoad && onLoad(e);
  }

  return (
    <div className={`${style.imageBox} ${className}`} style={{backgroundImage: `url(${src})`}}>
      <img src={src} onLoad={onLoadHandler} style={{display: 'none'}} alt={alt}/>
      { children }
    </div>
  );
};

export default Image;