import React from 'react';
import FontAwesome from 'react-fontawesome';
import moment from 'moment';

import style from './cardInfo.css';

/* 
---- PROPS
teamID
teams
date
 */
const CardInfo = (props) => {

  const teamName = (teams, teamID) => {
    let data = props.teams.find((item) => {
      return item.teamId === teamID;
    });

    if (!data) return '---';
    return data.name;
  }

  return (
    <div className={style.cardInfo}>
      <span className={style.teamName}>
        {teamName(props.teams, props.teamID)}
      </span>
      <span className={style.dateInfo}>
        <FontAwesome name="clock"/>
        {moment(props.date).format('MM/DD/YYYY')}
      </span>
    </div>
  );
};

export default CardInfo;