import React from 'react';
import { Link } from 'react-router-dom';


import CardInfo from '../CardInfo/cardInfo';
import style from './newsList.css';

const NewsListTemplates = (props) => {

  let template = null,
      items = props.items;

  switch(props.type) {
    case 'card':
      template = items.map((item, i) => (
        <div key={i} className={style.newsListItem}>
          <CardInfo
            date={item.date}
            teams={props.teams}
            teamID={item.team}
          />
          <Link to={`/articles/${item._id}`}>
            <h2>{item.title}</h2>
          </Link>
        </div>
      ));
      break;
    case 'news':
      template = items.map((item, i) => (
        <div key={i} className={style.newsListItem_news}>
          <Link to={`/articles/${item._id}`} style={{backgroundImage: `url('${item.image}')`}} className={style.newsListItem_news_left}>
            <div></div>
          </Link>
          <div className={`${style.newsListItem} ${style.newsListItem_news_right}`}>
            <CardInfo
              date={item.date}
              teams={props.teams}
              teamID={item.team}
            />
            <Link to={`/articles/${item._id}`}>
              <h2>{item.title}</h2>
            </Link>
          </div>
        </div>
      ));
      break;
    default:
      template = null;
  }

  return template;
};

export default NewsListTemplates;