import React from 'react';
import SlickSlider from 'react-slick';
import { Link } from 'react-router-dom';


import style from './newsSlider.css';


const SliderTemplates = (props) => {

  let items = props.data;
  let template = null;
  switch (props.type) {
    case 'featured': 
      template = items.map((item, i) => (
        <div key={i}>
          <div className={style.featuredItem} style={{backgroundImage: `url('${item.image}')`}}>
            <Link to={`/articles/${item._id}`} className={style.featuredCaption}>
              {item.title}
            </Link>
          </div>
        </div>
      ))
      break;
    default: 
     template = null;
  }


  let settings = {
    speed: 500,
    dots: false,
    arrows: false,
    infinite: true,
    slideToShow: 1,
    slidesToScroll: 1,
    ...props.settings
  }

  return (
    <SlickSlider {...settings}>
      {template}
    </SlickSlider>
  );
};

export default SliderTemplates;