import React from 'react';
import { Link } from 'react-router-dom';

import style from './videosList.css';
import CardInfo from '../CardInfo/cardInfo';

const VideosListTemplates = (props) => {

  let template = null;

  switch(props.type) {
    case 'card':
      template = props.videos.map((item, i) => (
        <div key={i} className={style.itemWrapper}>
          <Link 
            to={`/videos/${item._id}`}
            style={{backgroundImage: `url('${item.image}')`}}
            className={style.item_left}
          >
            <div></div>
          </Link>
          <div className={style.item_right}>
            <CardInfo 
              teams={props.teams} 
              teamID={item.team}
              date={item.date}
            />
            <Link to={`/videos/${item._id}`} className={style.itemTitle}>{item.title}</Link>
          </div>
        </div>
      ));
      break;
    default:
      template = null;
  }

  return template;
};

export default VideosListTemplates;